import React, { useState } from 'react';

const HappyNumbersLab = () => {
  const [inputNumber, setInputNumber] = useState('');
  const [happyNumbersCount, setHappyNumbersCount] = useState(null);

  const handleInputChange = (event) => {
    setInputNumber(event.target.value);
  };

  const calculateHappyNumbers = () => {
    const N = parseInt(inputNumber, 10);
    if (!isNaN(N)) {
      setHappyNumbersCount(countHappyNumbers(N));
    }
  };

  const countHappyNumbers = (N) => {
    let count = 0;
    for (let i = 1; i <= N; i++) {
      if (isHappyNumber(i)) {
        count++;
      }
    }
    return count;
  };

  const isHappyNumber = (number) => {
    while (number > 0) {
      const digit = number % 10;
      if (digit !== 4 && digit !== 7) {
        return false;
      }
      number = Math.floor(number / 10);
    }
    return true;
  };

  return (
    <div className="container">
      <h1>Лабораторна робота 1</h1>
      <h2>Умова задачі:</h2>
      <p>Всі знають, що щасливі числа – це ті числа, які містять лише щасливі цифри 7 та 4. Вам
        потрібно знайти кількість щасливих чисел невеликих N.</p>
      <h2>Вхідні дані</h2>
      <p>У єдиному рядку вхідного файлу INPUT.TXT записано натуральне число N, що не
        перевищує 1032.</p>
      <h2>Вихідні дані:</h2>
      <p>У єдиний рядок вихідного файлу OUTPUT.TXT потрібно вивести одне ціле число –
        відповідь на завдання.</p>
        <h2>Приклад:</h2>
      <p>INPUT: 56.</p>
      <p>OUTPUT: 4</p>
      <form>
        <div className="form-group">
          <label htmlFor="Input">Введіть число N:</label>
          <input type="number" value={inputNumber} onChange={handleInputChange} className="form-control" id="Input" />
        </div>
        <button type="button" onClick={calculateHappyNumbers} className="btn">Обчислити</button>
      </form>

      {happyNumbersCount !== null && (
        <div className="form-group">
          <label htmlFor="Output">Результат:</label>
          <input type="text" value={happyNumbersCount} className="form-control" id="Output" readOnly />
        </div>
      )}
    </div>
  );
};

export default HappyNumbersLab;
