import React, { useState } from 'react';

const PathFindingLab = () => {
  const [gridInput, setGridInput] = useState('');
  const [gridOutput, setGridOutput] = useState('');

  const handleInputChange = (event) => {
    setGridInput(event.target.value);
  };

  const calculatePath = () => {
    const grid = gridInput.split('\n').map(row => row.split(''));
    const output = performPathFinding(grid);
    setGridOutput(output.join('\n'));
  };

  const performPathFinding = (grid) => {
    const N = grid.length;
    let start, end;
    const visited = Array.from({ length: N }, () => Array(N).fill(false));
    const prev = Array.from({ length: N }, () => Array(N).fill(null));

    // Find start and end points
    for (let i = 0; i < N; i++) {
      for (let j = 0; j < N; j++) {
        if (grid[i][j] === '@') start = [i, j];
        if (grid[i][j] === 'X') end = [i, j];
      }
    }

    const queue = [start];
    visited[start[0]][start[1]] = true;

    while (queue.length > 0) {
      const [x, y] = queue.shift();
      
      const directions = [[0, 1], [0, -1], [-1, 0], [1, 0]];
      for (let [dx, dy] of directions) {
        const newX = x + dx;
        const newY = y + dy;

        if (isValid(newX, newY, N) && !visited[newX][newY] && (grid[newX][newY] === '.' || grid[newX][newY] === 'X')) {
          queue.push([newX, newY]);
          visited[newX][newY] = true;
          prev[newX][newY] = [x, y];
        }
      }
    }

    // Trace path
    if (visited[end[0]][end[1]]) {
      let current = end;
      while (current) {
        const [x, y] = current;
        grid[x][y] = '+';
        current = prev[x][y];
      }
      grid[start[0]][start[1]] = '@';
      return grid.map(row => row.join(''));
    } else {
      return ['No path found'];
    }
  };

  const isValid = (x, y, N) => {
    return x >= 0 && y >= 0 && x < N && y < N;
  };

  return (
    <div className="container">
      <h1>Лабораторна робота 3</h1>
      <h2>Умова задачі:</h2>
      <p>У таблиці N рядків і N стовпців деякі клітини зайняті кульками, інші вільні.
        Вибрано кульку, яку потрібно перемістити, і місце, куди її потрібно
        перемістити. Вибрана кулька за один крок переміщається у сусідню по
        горизонталі або вертикалі вільну клітину. Потрібно з'ясувати, чи можливо
        перемістити кульку з початкової клітини в задану, і, якщо можливо, знайти
        шлях з найменшої кількості кроків.</p>
      <h2>Вхідні дані</h2>
      <p> У першому рядку вхідного файлу INPUT.TXT знаходиться число N, у 
        наступних N рядках - N символів. Символом точки позначена вільна клітина, 
        англійської заголовної O - кулька, @@ - вихідне положення кульки, який 
        повинен рухатися, англійської заголовної X - кінцеве положення кульки. (2 ≤ 
        N ≤ 40)</p>
      <h2>Вихідні дані:</h2>
      <p> У вихідний файл OUTPUT.TXT виведіть у першому рядку Yes, якщо рух
        можливий, або No, якщо ні. Якщо рух можливий, то далі слід вивести N рядків
        N символів - як і на введенні, але букву X, а також всі точки по дорозі слід
        замінити плюсами. Якщо рішень кілька, виведіть будь-яке</p>
        <h2>Приклад:</h2>
      <p>INPUT:</p>
      <p>5</p>
      <p>....X</p>
      <p>.OOOO</p>
      <p>.....</p>
      <p>OOOO.</p>
      <p>@....</p>
      <p>OUTPUT:</p>
      <p>5</p>
      <p>+++++</p>
      <p>+OOOO</p>
      <p>+++++</p>
      <p>OOOO+</p>
      <p>@++++</p>
      
      <form>
        <div className="form-group">
          <label htmlFor="InputGrid">Введіть сітку:</label>
          <textarea value={gridInput} onChange={handleInputChange} className="form-control" id="InputGrid" rows="6"></textarea>
        </div>
        <button type="button" onClick={calculatePath} className="btn">Знайти шлях</button>
      </form>

      {gridOutput && (
        <div className="form-group">
          <label htmlFor="OutputGrid">Результат:</label>
          <textarea value={gridOutput} className="form-control" id="OutputGrid" rows="6" readOnly />
        </div>
      )}
    </div>
  );
};

export default PathFindingLab;
